	@Inject
	public ThingGrammarAccess(GrammarProvider grammarProvider) {
		this.grammar = internalFindGrammar(grammarProvider);
		this.pThingModel = new ThingModelElements();
		this.pModelPropertyContainer = new ModelPropertyContainerElements();
		this.pModelBridge = new ModelBridgeElements();
		this.pModelThing = new ModelThingElements();
		this.pModelChannel = new ModelChannelElements();
		this.pModelItemType = new ModelItemTypeElements();
		this.pBaseModelItemType = new BaseModelItemTypeElements();
		this.pModelProperty = new ModelPropertyElements();
		this.pCHANNEL_ID = new CHANNEL_IDElements();
		this.pUID = new UIDElements();
		this.pUID_SEGMENT = new UID_SEGMENTElements();
		this.pValueType = new ValueTypeElements();
		this.pBOOLEAN = new BOOLEANElements();
		this.pNUMBER = new NUMBERElements();
		this.tID = (TerminalRule) GrammarUtil.findRuleForName(getGrammar(), "org.openhab.core.model.thing.Thing.ID");
		this.tSTRING = (TerminalRule) GrammarUtil.findRuleForName(getGrammar(), "org.openhab.core.model.thing.Thing.STRING");
		this.tML_COMMENT = (TerminalRule) GrammarUtil.findRuleForName(getGrammar(), "org.openhab.core.model.thing.Thing.ML_COMMENT");
		this.tSL_COMMENT = (TerminalRule) GrammarUtil.findRuleForName(getGrammar(), "org.openhab.core.model.thing.Thing.SL_COMMENT");
		this.tWS = (TerminalRule) GrammarUtil.findRuleForName(getGrammar(), "org.openhab.core.model.thing.Thing.WS");
		this.tANY_OTHER = (TerminalRule) GrammarUtil.findRuleForName(getGrammar(), "org.openhab.core.model.thing.Thing.ANY_OTHER");
	}
	
	protected Grammar internalFindGrammar(GrammarProvider grammarProvider) {
		Grammar grammar = grammarProvider.getGrammar(this);
		while (grammar != null) {
			if ("org.openhab.core.model.thing.Thing".equals(grammar.getName())) {
				return grammar;
			}
			List<Grammar> grammars = grammar.getUsedGrammars();
			if (!grammars.isEmpty()) {
				grammar = grammars.iterator().next();
			} else {
				return null;
			}
		}
		return grammar;
	}
	
	@Override
	public Grammar getGrammar() {
		return grammar;
	}
	

	
